import { useState } from "react";
import logo from './logo.svg';
import './App.css'
import { TextField, Button } from "@mui/material";


function App() {
  const [taskName, setTaskName] = useState("");
  const [tasks, setTasks] = useState([])

  const valueChange = (event) => {
    console.log(event);
    const newValue = event.target.value;
    setTaskName(newValue);
  }

  const handleAddTask = (e, task) => {
    e.preventDefault();
    const newTasks = [...tasks];

    newTasks.unshift({
      id: task.length,
      name: task,
    });
    setTasks(newTasks);
  }


  return (
    <div className="App">
      <div id="todo-app">
        <form>
          <TextField type="text" id="new-task" placeholder="Enter a new task" value={taskName} onChange={valueChange} />
          <Button id="add-task" varient="outline" onClick={(e) => handleAddTask(e, taskName)}>create task</Button>

          <ul id="task-list">
            {tasks.map((task, index) => {
              return (
                <li key={index}>{task.name}</li>
              )
            })}
          </ul>
        </form>
      </div>
    </div>
  );
}

export default App;
